import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { FoodService } from 'src/app/services/food.service';
import { Food } from 'src/app/shared/models/Food';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  foods: Food[] = [];
  loading: boolean = true; // –§–ª–∞–≥ –∑–∞–≥—Ä—É–∑–∫–∏

  constructor(private foodService: FoodService, activatedRoute: ActivatedRoute) {
    let foodsObservalbe: Observable<Food[]>;

    activatedRoute.params.subscribe((params) => {
      if (params.searchTerm)
        foodsObservalbe = this.foodService.getAllFoodsBySearchTerm(params.searchTerm);
      else if (params.tag)
        foodsObservalbe = this.foodService.getAllFoodsByTag(params.tag);
      else
        foodsObservalbe = this.foodService.getAll();

      foodsObservalbe.subscribe((serverFoods) => {
        this.foods = serverFoods;
        this.loading = false; // –ó–∞–≤–µ—Ä—à–∞–µ–º –∑–∞–≥—Ä—É–∑–∫—É
      }, (error) => {
        this.loading = false; // –ó–∞–≤–µ—Ä—à–∞–µ–º –∑–∞–≥—Ä—É–∑–∫—É –ø—Ä–∏ –æ—à–∏–±–∫–µ
        console.error(error);
      });
    });
  }

  ngOnInit(): void {
  }

}
<div class="search-container">
  <app-search></app-search>
  <app-tags></app-tags>
</div>

<app-not-found [visible]="!foods || !foods.length" resetLinkText="Reset Search">
</app-not-found>

<div class="food-grid">
  <div class="food-card" *ngFor="let food of foods" @fadeInAnimation>
    <a routerLink="/food/{{food.id}}" class="food-link">
      <div class="food-image-container">
        <img [src]="food.imageUrl" [alt]="food.name" class="food-image" />
      </div>
      <div class="food-info">
        <h3 class="food-name">{{ food.name }}</h3>
        <div class="rating-container">
          <span class="favorite {{food.favorite ? '' : 'not'}}">‚ù§</span>
          <star-rating [stars]="food.stars"></star-rating>
        </div>
        <div class="meta">
          <div class="origins">
            <span *ngFor="let origin of food.origins" class="origin-tag">{{ origin }}</span>
          </div>
          <div class="cook-time">üïí {{ food.cookTime }}</div>
        </div>
        <div class="food-price">{{ food.price | currency }}</div>
      </div>
    </a>
  </div>
</div>
