import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { FoodService } from 'src/app/services/food.service';
import { Food } from 'src/app/shared/models/Food';
import { UpperCasePipe } from '@angular/common';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  providers: [UpperCasePipe]
})
export class HomeComponent implements OnInit {

  foods: Food[] = [];
  constructor(private foodService: FoodService, activatedRoute: ActivatedRoute, uppercasepipe: UpperCasePipe) {
    let foodsObservalbe:Observable<Food[]>;
    activatedRoute.params.subscribe((params) => {
      if (params.searchTerm)
        foodsObservalbe = this.foodService.getAllFoodsBySearchTerm(params.searchTerm);
      else if (params.tag)
        foodsObservalbe = this.foodService.getAllFoodsByTag(params.tag);
      else
        foodsObservalbe = foodService.getAll();

        foodsObservalbe.subscribe((serverFoods) => {
          this.foods = serverFoods;
        })
    })
  }

  ngOnInit(): void {
  }

}
<app-search></app-search>
<app-tags></app-tags>
<app-not-found
[visible]="!foods || !foods.length"
resetLinkText="Reset Search">
</app-not-found>
<div class="cards">
  <ul>
    <li *ngFor="let food of foods">
      <a routerLink="/food/{{food.id}}">
        <img [src]="food.imageUrl" [alt]="food.name" />
        <div class="content">
          <div class="name">
            {{food.name | uppercase}}
          </div>
          <div class="text">
            {{ food.text | uppercase}}
          </div>
          <div class="price">
            <span>
              {{food.price | currency}}
            </span>
          </div>
          <div class="product-item-footer">
            <div class="origins">
              <span *ngFor="let origin of food.origins">
                {{origin | uppercase}}
              </span>
            </div>
            <div class="cook-time">
              <span>🕒</span>
              {{food.cookTime}}
            </div>
          </div>
        </div>
        <button click="$event.stopPropagation();" class="card-button">Add To Cart</button>
      </a>
    </li>
  </ul>
</div>
